mapper.select:
SELECT distinct bt.taskName,bss.smallSampleNum,bm.realCapacity, bm.qualityGrade, bsc.pingjunzhi shuifen_pingjunzhi, bzbc.zazhizongliang_1, bzbc.buwanshanlihanliang_pingjunzhi_1, bzbc.shengmeilihanliang_pingjunzhi, bcc.sezeqiwei_pingjunzhi, bzc.pingjunzhi zhifangsuanzhi_pingjunzhi, byp.pinchangpingfenzhi FROM barn_sample bs LEFT JOIN barn_small_sample bss ON bss.sampleId = bs.id LEFT JOIN barn_task bt ON bss.taskId = bt.id LEFT JOIN barn_manuscript bm ON bm.sampleId = bs.id LEFT JOIN barn_shuifen_cedingjilu bsc ON bsc.smallSampleId = bss.id LEFT JOIN barn_zazhi_buwanshanli_cedingjilu bzbc ON bzbc.smallSampleId = bss.id LEFT JOIN barn_check_cedingjilu bcc ON bcc.smallSampleId = bss.id LEFT JOIN barn_zhifangsuanzhi_cedingjilu bzc ON bzc.smallSampleId = bss.id LEFT JOIN barn_yumipinchang_pingfenjilu byp ON byp.smallSampleId = bss.id
where bss.sampleId=#{id}
GROUP BY bs.id
