mapper.select:
SELECT distinct bt.taskName, bm.qualityGrade,bss.smallSampleNum, bsc.pingjunzhi shuifen_pingjunzhi, bzbc.zazhizongliang_1, bzbc.kuangwuzhihanliang_pingjunzhi, bzbc.buwanshanlihanliang_pingjunzhi_1, bcc.yingduzhishu_pingjunzhi, bcc.sezeqiwei_pingjunzhi, bmc.pingjunzhiganmianjinzhiliang, bcc.shimianjin_pingjunzhi, bmp.pinchangpingfenzhi , bs.id sampleId,bs.sampleNum FROM barn_sample bs LEFT JOIN barn_small_sample bss ON bss.sampleId = bs.id LEFT JOIN barn_task bt ON bss.taskId = bt.id LEFT JOIN barn_manuscript bm ON bm.sampleId = bs.id LEFT JOIN barn_shuifen_cedingjilu bsc ON bsc.smallSampleId = bss.id LEFT JOIN barn_zazhi_buwanshanli_cedingjilu bzbc ON bzbc.smallSampleId = bss.id LEFT JOIN barn_check_cedingjilu bcc ON bcc.smallSampleId = bss.id LEFT JOIN barn_mianjinxishuiliang_cedingjilu bmc ON bmc.smallSampleId = bss.id LEFT JOIN barn_mantoupinchang_pingfenjilu bmp ON bmp.smallSampleId = bss.id
where bss.sampleId=#{id}
GROUP BY bs.id
